import Todolist from "./components/Todolist";
import { nanoid } from 'nanoid';
import { useState } from "react";

function App() {
  const [todolists, setTodolists] = useState([
    {
      id: nanoid(),
      title: "Todolist 1",
      tasks: [{ id: nanoid(), title: "Faire les courses", isDone: true },
      { id: nanoid(), title: "Nourrir le chat", isDone: false },
      { id: nanoid(), title: "Faire des pancakes", isDone: false }
      ]
    },
    {
      id: nanoid(),
      title: "Todolist 2",
      tasks: [{ id: nanoid(), title: "Corde à sauter", isDone: true },
      { id: nanoid(), title: "Shadow", isDone: false },
      { id: nanoid(), title: "Travail au sac", isDone: true },
      { id: nanoid(), title: "Renfo", isDone: false }
      ]
    }
  ]);

  function updateTodo(todoUpdated) {
    let pos = todolists.findIndex((todo => todo.id === todoUpdated.id));
    if (pos > -1) {
      todolists.splice(pos, 1, todoUpdated);
      setTodolists([...todolists]);
    }
  }

  return (
    <main>
      <h1>TodoApp</h1>
      <div className="todolist-container">
        {todolists.map((todo) => (
          <Todolist key={todo.id} todo={todo} updateTodo={updateTodo} />
        ))}
      </div>
    </main>
  );
}

export default App;
