import React, { useState } from 'react';
import '../styles/colors.scss';

function DarkModeToggle(props) {
    // let initialTheme = false;
    // if (JSON.parse(localStorage.getItem('DARK_MODE')) === true) {
    //     document.body.classList.add('dark-mode');
    //     initialTheme = true;
    // }

    //const [darkMode, setDarkMode] = useState(initialTheme || false);
    console.log(JSON.parse(localStorage.getItem('DARK_MODE')));
    const [darkMode, setDarkMode] = useState(JSON.parse(localStorage.getItem('DARK_MODE')));


    // this.state = {
    //     darkMode: JSON.parse(localStorage.getItem('DARK_MODE'))
    // }

    //this.handleModeChange = this.handleModeChange.bind(this);

    function handleModeChange() {
        //if (!this.state.darkMode) {
        if (!darkMode) {
            document.body.classList.add('dark-mode');
        } else {
            document.body.classList.remove('dark-mode');
        }

        setDarkMode(!darkMode);
        localStorage.setItem('DARK_MODE', !this.state.darkMode);
    }
    return (
        <div>
            <button onClick={handleModeChange}>Change Mode</button>
        </div>
    );
};

export default DarkModeToggle;
