import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


function TodoItem(props) {
    const task = props.task;
    function onCheck(event) {
        props.updateTask({ ...task, isDone: !task.isDone });
    }
    function onDelete() {
        props.deleteTask(task);
        console.log("suppression...");
    }
    return (
        <li className="todolist-item">
            <input type="checkbox" className="todolist-item__ckbx" id={task.id} defaultChecked={task.isDone} onChange={onCheck} />
            <label className="todolist-item__label" htmlFor={task.id}>{task.title}</label>
            <button type="button" title="Supprimer cette tâche" onClick={onDelete}>❌</button>
        </li>
    );
}

export default TodoItem;
