function TodoItem(props) {
    const task = props.task;
    return (
        <li className="todolist-item">
            <input type="checkbox" className="todolist-item__ckbx" id={task.id} checked={task.isDone} />
            <label className="todolist-item__label" htmlFor={task.id}>{props.title}</label>
        </li>
    );
}

export default TodoItem;
